import React, {Component} from "react"
import classes from "./Quiz.module.css"
import ActiveQuiz from "../../Components/ActiveQuiz/ActiveQuiz"

class Quiz extends Component{
	constructor(props){
		super(props);
		this.state = {
			answerState: null,
			activeQuestion: 0,
			quiz: [
				{
					id: 1,
					question: "Кто такой Пушкин?",
					rightAnswerId: 4, 
					answers: [
						{text: "Музыкант", id: 1},
						{text: "Архитектор", id: 2},
						{text: "Программист из Google", id: 3},
						{text: "Поэт", id: 4}
					]
				},
				{
					id: 2,
					question: "Кто был первым президентом США?",
					rightAnswerId: 1, 
					answers: [
						{text: "Джордж Вашингтон", id: 1},
						{text: "Дональд Трамп", id: 2},
						{text: "Владимир Путин", id: 3},
						{text: "Барак Обама", id: 4}
					]
				},
				{
					id: 3,
					question: "Кто создал язык программирования Python?",
					rightAnswerId: 2, 
					answers: [
						{text: "Билл Гейтс", id: 1},
						{text: "Гвидо Ван Россум", id: 2},
						{text: "Стив Джобс", id: 3},
						{text: "Илон Маск", id: 4}
					]
				}
			]
		}
	}
	
	onAnswerClickHandler = answerId =>{
		const question = this.state.quiz[this.state.activeQuestion];

		if(question.rightAnswerId === answerId){
			this.setState({
				answerState: {[answerId]: "success"}
			});

			if(!this.isQuizFinished()){
				const timeout = setTimeout(()=>{
					this.setState({
						activeQuestion: this.state.activeQuestion + 1,
						answerState: null
					});

					clearTimeout(timeout);
				}, 4000)
			}
			else{
				console.log("Finished!");
			}
		}
		else{
			this.setState({
				answerState: {[answerId]: "error"}
			})
		}
	}

	isQuizFinished = () => {
		return this.state.activeQuestion + 1 === this.state.quiz.length;
	}

	render(){
		return(
			<div className={classes.Quiz}>
				<div className={classes.QuizWrapper}>
					<h1>Ответьте на все вопросы</h1>
					<ActiveQuiz 
						answers={this.state.quiz[this.state.activeQuestion].answers}
						question={this.state.quiz[this.state.activeQuestion].question}
						rightAnswerId={this.state.quiz[this.state.activeQuestion].rightAnswerId}
						onAnswerClickHandler={this.onAnswerClickHandler}
						activeQuestion={this.state.activeQuestion}
						quizLength={this.state.quiz.length}
						state={this.state.answerState}
					/>
				</div>
			</div>
		)
	}
}

export default Quiz
