import React, {Component} from "react"
import classes from "./Auth.module.css"
import Button from "../../Components/UI/Button/Button"
import Input from "../../Components/UI/Input/Input"

class Auth extends Component{
	state = {
		formControls: {
			email: {
				type: "email",
				value: "",
				label: "Email",
				errorMessage: "Введите корректный Email",
				touched: false,
				valid: false,
				validation: {
					required: true,
					email: true
				}
			},
			password: {
				type: "password",
				value: "",
				label: "Пароль",
				errorMessage: "Введите корректный пароль",
				touched: false,
				valid: false,
				validation: {
					required: true,
					minLength: 6
				}
			}
		}
	}

	renderInputs = () =>{
		const inputs = Object.keys(this.state.formControls).map((inputName, index) =>{
			const input = this.state.formControls[inputName];
			return <Input
						type={input.type}
						
					/>
		});

	}

	onLoginHandler = () => {

	}

	onRegisterHandler = () => {

	}

	onSubmitHandler = evt =>{
		evt.preventDefault();
	}

	render(){
		return(
			<div className={classes.Auth}>
				<div>
					<h1>Авторизация</h1>
					<form onSubmit={this.onSubmitHandler} className={classes.AuthForm}>
						<Input label="Email"/>
						<Input label="Пароль" errorMessage="Test"/>
						<Button type="success" onClick={this.onLoginHandler}>Авторизоватся</Button>
						<Button type="primary" onClick={this.onRegisterHandler}>Зарегестрироватся</Button>
					</form>
				</div>	
			</div>
		)
	}
}

export default Auth
