import React, {Component} from "react"
import classes from "./Auth.module.css"
import Button from "../../Components/UI/Button/Button"
import Input from "../../Components/UI/Input/Input"

class Auth extends Component{
	state = {
		formControls: {
			email: {
				type: "email",
				value: "",
				label: "Email",
				errorMessage: "Введите корректный Email",
				touched: false,
				valid: false,
				validation: {
					required: true,
					email: true
				}
			},
			password: {
				type: "password",
				value: "",
				label: "Пароль",
				errorMessage: "Введите корректный пароль",
				touched: false,
				valid: false,
				validation: {
					required: true,
					minLength: 6
				}
			}
		}
	}

	renderInputs = () =>{
		return Object.keys(this.state.formControls).map((controlName, index) =>{
			const input = this.state.formControls[controlName];
			return(
				<Input
					key={controlName + index}
					type={input.type}
					value={input.value}
					label={input.label}
					errorMessage={input.errorMessage}
					touched={touched}
					valid={valid}
					shouldValidate={!!controlName.validation}
				/>
			)

		});

	}

	onLoginHandler = () => {

	}

	onRegisterHandler = () => {

	}

	onSubmitHandler = evt =>{
		evt.preventDefault();
	}

	render(){
		return(
			<div className={classes.Auth}>
				<div>
					<h1>Авторизация</h1>
					<form onSubmit={this.onSubmitHandler} className={classes.AuthForm}>
						{this.renderInputs()}
						<Button type="success" onClick={this.onLoginHandler}>Авторизоватся</Button>
						<Button type="primary" onClick={this.onRegisterHandler}>Зарегестрироватся</Button>
					</form>
				</div>	
			</div>
		)
	}
}

export default Auth
