import { FINISH_CREATE_QUIZ, CREATE_QUIZ_QUESTION } from "../actions/actionTypes"

const initialState = {
    quiz: []
}

export default function createReducer(state = initialState, action) {
    switch (action.type) {
        case CREATE_QUIZ_QUESTION: return {
            quiz: [...state.quiz, action.item]
        }
        case FINISH_CREATE_QUIZ: return {
            quiz: []
        }
        default: return state
    }
}
