import axios from "axios"

export function auth(email, password, isLogin) {
    return async dispatch => {
        const authData = {
            email: this.state.formControls.email.value,
            password: this.state.formControls.password.value,
            returnSecureToken: true
        }

        if (isLogin) {
            const url = "https://identitytoolkit.googleapis.com/v1/accounts:signInWithPassword?key=AIzaSyCR6XVI_5wH5fn4NcjA63kVMFqZsZcityE"
        }
        else { }
        const url = "https://identitytoolkit.googleapis.com/v1/accounts:signUp?key=AIzaSyCR6XVI_5wH5fn4NcjA63kVMFqZsZcityE"

        response = await axios.post(url, authData);
        data = response.data;
        console.log(data)
    }
}
